import React, { useContext } from 'react'
import ArrowNext from '../arrowNext/ArrowNext'
import ArrowPrev from '../arrowPrev/ArrowPrev'
import CounterBar from '../counterBar/CounterBar'
import CloseButton from '../closeButton/CloseButton'
import { CounterContext, PageHistory, UserAnswer } from '../../../context'
import {css, StyleSheet} from 'aphrodite'

const Header = (props) => {
  let { counter, setCounter } = useContext(CounterContext)
  let {setPages } = useContext(PageHistory)
  let { ans, setAns } = useContext(UserAnswer)
  const pagesAll = 10

  function comeBackStartPage() {
    setCounter(0)
    window.sessionStorage.setItem('counter', JSON.stringify(0))
    setPages(pages => [])
    window.sessionStorage.setItem('pages', JSON.stringify([]))
    setAns(ans => Object.assign({}, {
    }))
    window.sessionStorage.setItem('ans', JSON.stringify(Object.assign({}, ans, {
      'gender': 0,
      'eyewear_type': 0,
      'lenstype': 0,
      'frame_size': 0,
      'blue_light': null,
      'shade': '',
      'face_shape': '',
      'facial_features': '',
      'shape': '',
      'brand': ''
    })))

  }
  const styles = StyleSheet.create({
    header: {
      maxWidth: "inherit",
      minWidth: "inherit",
      width: "100%",
      backgroundColor: "#fff",
      maxHeight: "66px",
      background: "#FFFFFF",
      boxShadow: "0px 1px 7px rgba(58, 72, 80, 0.07)",
      boxSizing: 'border-box'
    },
    header__title_counter: {
      width: "38px",
      height: "19px",
      fontWeight: '400',
      fontSize: "18px",
      lineHeight: "19px",
      textAlign: "center",
      color: "#3C5060",
      margin: '0'
    },
    header__top: {
      display: "flex",
      alignItems: "center",
      justifyContent: "space-between",
    }
  }) 
  return (
    <header className={css(styles.header)}>
      <div className={css(styles.header__top)} style={(counter === 11 || counter === 0) ? { padding: '14px 25px 14px 25px' } : { padding: '20px 25px 19px 25px' }}>
        {
          (counter >= 1 && counter < 11) ? (
            <ArrowPrev></ArrowPrev>
          ) : (null)
        }
        {
          (counter <= 0 || counter === 11)
            ? (<img src={'data:image/svg+xml;base64,'} alt="" />)
            : (<h3 className={css(styles.header__title_counter)}>{`${counter}/${pagesAll}`}</h3>)
        }
        {
          (counter <= 0)
            ? (<ArrowNext></ArrowNext>)
            : (<CloseButton onClick={comeBackStartPage}></CloseButton>)
        }
      </div>
      {
        (counter >= 1)
          ?
          (
            <CounterBar value={counter} pages={pagesAll} />
          )
          :
          (null)
      }
    </header>
  )
}

export default Header