import React, { useContext, useRef } from 'react'
import classes from './Page10.module.css'
import Slider from '../../components/UI/slider/Slider'
import TitlePage from '../../components/UI/titlePage/TitlePage'
import SliderItem from '../../components/UI/sliderItem/SliderItem'
import { Link } from 'react-router-dom'
import { CounterContext, UserAnswer, PageHistory } from '../../context'
import {css, StyleSheet} from 'aphrodite/no-important'

function Page10() {
  const page = 'Page10'
  let { ans, setAns } = useContext(UserAnswer)
  let { pages, setPages } = useContext(PageHistory)
  let { counter, setCounter } = useContext(CounterContext)
  let button = useRef()
  let count = 0
  let selectBrand = []

  const nextPage = (event) => {
    if (count > 0) {
      setCounter(counter += 1)
      setPages(pages => [...pages, page])
      setAns(ans => Object.assign({}, ans, { 'brand': selectBrand.join(',') }))
      window.sessionStorage.setItem('ans', JSON.stringify(Object.assign({}, ans, { 'brand': selectBrand.join(',') })))
      window.sessionStorage.setItem('counter', JSON.stringify(counter))
      window.sessionStorage.setItem('pages', JSON.stringify([...pages, page]))
    }
    else {
      event.preventDefault()
    }
  }
  const styles = StyleSheet.create({
    page :{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        boxSizing: 'border-box',
        textDecoration: 'none',
    },
    btn: {
        width: '181px',
        height: '48px',
        backgroundColor: '#DEDEDE',
        borderRadius: '24px',
        fontStyle: 'normal',
        fontWeight: '700',
        fontSize: '18px',
        lineHeight: '25px',
        color: '#FFFFFF',
        border: 'none',
        boxShadow: '0px 1px 4px 1px rgba(0, 0, 0, 0.1)',
    },
    btnSelected: {
        background: 'linear-gradient(270deg, #45C7FA 0%, #2196F3 100%) !important',
    },
    selected: {
      border: '1.96px solid #2196F3 !important',
      position: 'relative',
      zIndex: '0'
  }
  })
  const classBtn = css(
    (count > 0) ? styles.btnSelected : styles.btn
  )
  return (
    <section onClick={(event) => {
      if (event.target?.getAttribute('id') === 'item' || event.target?.closest('#item')?.getAttribute('id') === 'item') {
        let sliderElem = (event.target?.getAttribute('id') === 'item') ? event.target : event.target?.closest('#item')
        if (sliderElem.getAttribute('id') === 'item') {
          if (sliderElem.classList.contains('selected')) {
            count -= 1
            sliderElem.classList.remove('selected')
            sliderElem.style.cssText = `none`
            selectBrand.pop(sliderElem.getAttribute('name'))
          }
          else {
            count += 1
            sliderElem.style.cssText = `
            border: 1.96px solid #2196F3 !important;
            position: relative;
            zIndex: 0;
            `
            sliderElem.classList.add('selected')
            selectBrand.push(sliderElem.getAttribute('name'))
          }
          (count > 0) ? button.current.style.cssText = `
          width: 181px;
          height: 48px;
          backgroundColor: #DEDEDE;
          borderRadius: 24px;
          fontStyle: normal;
          fontWeight: 700;
          fontSize: 18px;
          lineHeight: 25px;
          color: #FFFFFF;
          border: none;
          boxShadow: 0px 1px 4px 1px rgba(0, 0, 0, 0.1);
          background: linear-gradient(270deg, #45C7FA 0%, #2196F3 100%) !important;
          ` : button.current.style.cssText = `
          height: 48px;
          backgroundColor: #DEDEDE;
          borderRadius: 24px;
          fontStyle: normal;
          fontWeight: 700;
          fontSize: 18px;
          lineHeight: 25px;
          color: #FFFFFF;
          border: none;
          boxShadow: 0px 1px 4px 1px rgba(0, 0, 0, 0.1);
          `
        }
      }
    }} className={css(styles.page)}>
      <TitlePage style={{ margin: '30px 0 0 0' }} text={"Choose your favorite brands"} />
      <h4 style={{
        margin: '10px 0 20px 0', fontStyle: 'normal',
        fontWeight: '400',
        fontSize: '16px',
        lineHeight: '30px',
        color: '#697580'
      }}>You can pick more than one.</h4>

      <Slider>
        <SliderItem url={'data:image/png;base64,'} id="item" name='ray_ban' />
        <SliderItem url={'data:image/png;base64,'} id="item" name='oakley' />
        <SliderItem url={'data:image/png;base64,'} id="item" name='gucci' />
        <SliderItem url={'data:image/png;base64,'} id="item" name='armani_exchange' />

      </Slider>
      <Slider>
        <SliderItem url={'data:image/png;base64,'} id="item" name='hilary_duff' />
        <SliderItem url={'data:image/png;base64,'} id="item" name='prada' />
        <SliderItem url={'data:image/png;base64,'} id="item" name='versage' />
        <SliderItem url={'data:image/png;base64,'} id="item" name='vogue' />

      </Slider>
      <Slider>
        <SliderItem url={'data:image/png;base64,'} id="item" name='michael_kors' />
        <SliderItem url={'data:image/png;base64,'} id="item" name='coach' />
        <SliderItem url={'data:image/png;base64,'} id="item" name='tory_burch' />
        <SliderItem url={'data:image/png;base64,'} id="item" name='burberry' />
      </Slider>
      <Link style={{textDecoration: 'none'}}to="/Page11">
        <button onClick={nextPage} ref={button} style={{ marginTop: '14px',
         cursor: ' pointer',
        width: '181px',
        height: '48px',
        backgroundColor: '#DEDEDE',
        borderRadius: '24px',
        fontStyle: 'normal',
        fontWeight: '700',
        fontSize: '18px',
        lineHeight: '25px',
        color: '#FFFFFF',
        border: 'none',
        boxShadow: '0px 1px 4px 1px rgba(0, 0, 0, 0.1)',
   }} className={classBtn}>Continue</button>
      </Link>
    </section>
  )
}

export default Page10