import React, { useContext, useRef, useState } from 'react'
import classes from './Page8.module.css'
import Slider from '../../components/UI/slider/Slider'
import TitlePage from '../../components/UI/titlePage/TitlePage'
import SliderItem from '../../components/UI/sliderItem/SliderItem'
import { Link } from 'react-router-dom'
import { CounterContext, UserAnswer, PageHistory } from '../../context'
import {css, StyleSheet} from 'aphrodite/no-important'

function Page8(props) {
  const page = 'Page8'
  let { ans, setAns } = useContext(UserAnswer)
  let { pages, setPages } = useContext(PageHistory)
  let { counter, setCounter } = useContext(CounterContext)


  let button = useRef()
  let count = 0
  let selectGlass = []

  const nextPage = (event) => {
    if (count > 0) {
      setCounter(counter += 1)
      setPages(pages => [...pages, page])
      setAns(ans => Object.assign({}, ans, {'shape': selectGlass.join(',')}))
      window.sessionStorage.setItem('ans', JSON.stringify(Object.assign({}, ans, {'shape': selectGlass.join(',')})))
      window.sessionStorage.setItem('counter', JSON.stringify(counter))
      window.sessionStorage.setItem('pages', JSON.stringify([...pages, page]))
    }
    else {
      event.preventDefault()
    }
  }

  const styles = StyleSheet.create({
    page :{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        boxSizing: 'border-box',
        textDecoration: 'none',
    },
    btn: {
        width: '181px',
        height: '48px',
        backgroundColor: '#DEDEDE',
        borderRadius: '24px',
        fontStyle: 'normal',
        fontWeight: '700',
        fontSize: '18px',
        lineHeight: '25px',
        color: '#FFFFFF',
        border: 'none',
        boxShadow: '0px 1px 4px 1px rgba(0, 0, 0, 0.1)',
    },
    btnSelected: {
      width: '181px',
        height: '48px',
        backgroundColor: '#DEDEDE',
        borderRadius: '24px',
        fontStyle: 'normal',
        fontWeight: '700',
        fontSize: '18px',
        lineHeight: '25px',
        color: '#FFFFFF',
        border: 'none',
        boxShadow: '0px 1px 4px 1px rgba(0, 0, 0, 0.1)',
        background: 'linear-gradient(270deg, #45C7FA 0%, #2196F3 100%) !important',
    },
    selected: {
      border: '1.96px solid #2196F3 !important',
      position: 'relative',
      zIndex: '0'
  }
  })
  const classBtn = css(
    (count > 0) ? styles.btnSelected : styles.btn
  )
  
  return (
    <section onClick={(event) => {
      if (event.target?.getAttribute('id') === 'item' || event.target?.closest('#item')?.getAttribute('id') === 'item') {
        let sliderElem = (event.target?.getAttribute('id') === 'item') ? event.target : event.target?.closest('#item')
        if (sliderElem.getAttribute('id') === 'item') {
          if (sliderElem.classList.contains('selected')) {
             count -= 1 
            sliderElem.classList.remove('selected')
            sliderElem.style.cssText = `none`
            selectGlass.pop(sliderElem.getAttribute('name'))
            sliderElem.childNodes[2].style.opacity = '0'
          }
          else {
            count += 1 
            sliderElem.style.cssText = `
            border: 1.96px solid #2196F3 !important;
            position: relative;
            zIndex: 0;
            `
            sliderElem.classList.add('selected')
            selectGlass.push(sliderElem.getAttribute('name'))
            sliderElem.childNodes[2].style.opacity = '100%'
          }

          (count > 0) ? button.current.style.cssText = `
          width: 181px;
          height: 48px;
          backgroundColor: #DEDEDE;
          borderRadius: 24px;
          fontStyle: normal;
          fontWeight: 700;
          fontSize: 18px;
          lineHeight: 25px;
          color: #FFFFFF;
          border: none;
          boxShadow: 0px 1px 4px 1px rgba(0, 0, 0, 0.1);
          background: linear-gradient(270deg, #45C7FA 0%, #2196F3 100%) !important;
          cursor: pointer;
          ` : button.current.style.cssText = `
          height: 48px;
          backgroundColor: #DEDEDE;
          borderRadius: 24px;
          fontStyle: normal;
          fontWeight: 700;
          fontSize: 18px;
          lineHeight: 25px;
          color: #FFFFFF;
          border: none;
          boxShadow: 0px 1px 4px 1px rgba(0, 0, 0, 0.1);
          cursor: pointer;
          `
        }
      }
    }} className={css(styles.page)}>
      <TitlePage style={{ margin: '30px 0 0 0' }} text={"Which frame style are you looking for?"} />
      <h4 style={{
        margin: '10px 0 20px 0', fontStyle: 'normal',
        fontWeight: '400',
        fontSize: '16px',
        lineHeight: '30px',
        color: '#697580'
      }}>You can pick more than one.</h4>

      <Slider>
        <SliderItem url={'data:image/png;base64,'} text={"Rectangle"} name="rectangle" id="item" />
        <SliderItem url={'data:image/png;base64,'} text={"Browline"} name="browline" id="item" />
        <SliderItem url={'data:image/png;base64,'} text={"Aviator"} name="aviator" id="item" />
        <SliderItem url={'data:image/png;base64,'} text={"Geometric"} name="geometric" id="item" />

      </Slider>
      <Slider>
        <SliderItem url={'data:image/png;base64,'} text={"Wayframe"} name="wayframe" id="item" />
        <SliderItem url={'data:image/png;base64,'} text={"Round"} name="round" id="item" />
        <SliderItem url={'data:image/png;base64,'} text={"Oval"} name="oval" id="item" />
        <SliderItem url={'data:image/png;base64,'} text={"Oversized"} name="oversized" id="item" />

      </Slider>
      <Slider>
        <SliderItem url={'data:image/png;base64,'} text={"Cat Eye"} name="cat_eye" id="item" />
        <SliderItem url={'data:image/png;base64,'} text={"Rimless"} name="rimless" id="item" />
        <SliderItem url={'data:image/png;base64,'} text={"Square"} name="square" id="item" />
        <SliderItem url={'data:image/png;base64,'} text={"Wrap"} name="wrap" id="item" />
      </Slider>
      <Link style={{textDecoration: 'none'}}to="/Page9">
        <button onClick={nextPage} ref={button} style={{ marginTop: '14px',
         cursor: 'pointer',
        width: '181px',
        height: '48px',
        backgroundColor: '#DEDEDE',
        borderRadius: '24px',
        fontStyle: 'normal',
        fontWeight: '700',
        fontSize: '18px',
        lineHeight: '25px',
        color: '#FFFFFF',
        border: 'none',
        boxShadow: '0px 1px 4px 1px rgba(0, 0, 0, 0.1)',
   }} className={classBtn}>Continue</button>
      </Link>
    </section>

  )
}

export default Page8